import React, { useState, useEffect } from "react";
import { Config } from "../../constant/Index";
function Favourite() {
  const [data, setData] = useState();
  const [fdata, setFdata] = useState(); 
  const fetchDatap = async () => {
    try {
      const response = await fetch(Config.serverUrlProduct);
      const jsonData = await response.json();
      setData(jsonData.data);
      console.log("data")
      console.log(data);
    } catch (error) {
      console.error("Error fetching data:", error);
    }
  };
  const fetchDataf = async () => {
    try {
      const token = '241|99nrXNA44oarRLr4QFJueCwNxSabtqMuMlBAeDlV';
      const response = await fetch(Config.serverUrlMe, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      const jsonData = await response.json();
      setFdata(jsonData.data.user.favourites);
      console.log("fdata")    
      console.log(fdata); 
    } catch (error) { 
      console.error("Error fetching data:", error);
    }
  };
  
  useEffect(() => { 
    fetchDatap();  
    fetchDataf();
  }, []);

  

  if (!data || data.length === 0) {
    return <div>Loading...</div>;
  }
  

  return (
    <>
        
      <div className="menu__section">
      <h1 style={{"color":"#FE734D"}}>Favourite</h1>
        <div className="menu__cards">
          {fdata.map((data)=>
        
              data.map((data) => (
                <div key={data.id} className="card">
                  {/* card rating */}
                  <div className="rating">
                    <h4>{data?.stars}</h4>
                    <i className="fa-solid fa-star" />
                    <span>({data?.cooking_time}+)</span>
                  </div>
                  <div className="heart">
                    <i
                      className="fa-regular fa-heart"
                      onClick="changeClass(this)"
                    />
                  </div>
                  <img
                    src={`https://danzee.fra1.digitaloceanspaces.com/dzfood/admin/images/products/large/${data.image}`}
                    alt="product img"
                  />
                  <div className="menu__content">
                    <div className="title__price">
                      <h3 className="card__title">{data?.title?.en}</h3>
                      <span className="price">
                        Rs {data?.price ? data?.price : 0}
                      </span>
                    </div>
                    <div className="time">
                      <i className="fa-solid fa-stopwatch" />
                      <span>10-15 mins</span>
                    </div>
                    <div className="food__type">
                      <span className="type">Burger</span>
                      <span className="type">Chicken</span>
                      <span className="type">Fast Food</span>
                    </div>
                  </div>
                </div>
              
            )

          ))}
        </div>
      </div>
    </>
  );
}

export default Favourite;
