import React, { useEffect, useState } from "react";
import "./cart.css";

function Cart() {
  const [cartItems, setCartItems] = useState([]);

  useEffect(() => {
    const storedCartItems = JSON.parse(localStorage.getItem("cartItems")) || [];
    setCartItems(storedCartItems);
  }, []);

  function renderCartItem(item) {
    return (
      <div key={item.id} className="cart__item">
        <img src={`https://danzee.fra1.digitaloceanspaces.com/dzfood/admin/images/products/large/${item.image}`} alt="product image" />
        <div className="item__details">
          {/* <h4>{item.title.en}</h4> */}
          <p>Price: Rs {item.price}</p>
          <p>Quantity: {item.quantity}</p>
          <p>Size: {item.size}</p>
          {item.accessories.length > 0 && (
            <div className="accessories">
              <h5>Accessories:</h5>
              <ul>
                {item.accessories.map((accessory) => (
                  <li key={accessory.id}>
                    {accessory.title.en} (Quantity: {accessory.quantity})
                  </li>
                ))}
              </ul>
            </div>
          )}
        </div>
      </div>
    );
  }

  return (
    <div className="cart__section">
      <div className="order__detail">
        {/* ... */}
      </div>
      <div className="cart__items">
        <h3 className="subheading">
          <i className="bx bx-chevron-left" />
          Items From Your Cart
        </h3>
        {cartItems.length === 0 ? (
          <>
            <img src="img/cartempty.png" alt="empty cart img" />
            <p className="paragraph">You haven't added any items in your cart yet.</p>
            <a href="menu.html" className="btn">
              Explore Menu
            </a>
          </>
        ) : (
          cartItems.map(renderCartItem)
        )}
      </div>
    </div>
  );
}

export default Cart;
