import React, { useEffect, useState } from "react";
import { Config } from "../../constant/Index";
import "./popup.css";

function Popup(props) {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [img, setImg] = useState();
  const [quantity, setQuantity] = useState(1); // State for quantity
  const [accessoryQuantities, setAccessoryQuantities] = useState({}); // State for accessory quantities

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch(
          `${Config.serverUrlProduct}/${props.sendid}`
        );
        const jsonData = await response.json();
        setData(jsonData.data);
        setLoading(false);
        setImg(
          jsonData?.data?.image
            ? Config.digitalOceanLinkProductLargeImg + jsonData?.data?.image
            : "https://upload.wikimedia.org/wikipedia/commons/d/d1/Image_not_available.png"
        );
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
  }, [props.sendid]);

  const popup = document.getElementById("popup");

  function closePopup() {
    if (popup) {
      popup.style.visibility = "hidden";
    }
  }

  function handleQuantityDecrease() {
    if (quantity > 1) {
      setQuantity(quantity - 1);
    }
  }

  function handleQuantityIncrease() {
    setQuantity(quantity + 1);
  }

  function handleAccessoryQuantityDecrease(accessoryId) {
    if (accessoryQuantities[accessoryId] > 1) {
      setAccessoryQuantities((prevQuantities) => ({
        ...prevQuantities,
        [accessoryId]: prevQuantities[accessoryId] - 1
      }));
    }
  }

  function handleAccessoryQuantityIncrease(accessoryId) {
    setAccessoryQuantities((prevQuantities) => ({
      ...prevQuantities,
      [accessoryId]: (prevQuantities[accessoryId] || 0) + 1
    }));
  }

  return (
    <>
      <div className="card__popup" id="popup">
        {/* Existing code for crossicon */}
        <div className="popup__product">
          <div className="popup__content">
            {/* Existing code for img, popup title, paragraph */}
            <div className="add__remove">
              {/* Existing code for quantity decrease */}
              <span className="">{quantity}</span>
              {/* Existing code for quantity increase */}
            </div>
            {/* Existing code for popup price */}
            <div className="accessories">
              <div className="radios">
                {/* Existing code for accessory radio buttons */}
              </div>
              {data?.accessories?.[0] ? (
                <div className="accessory">
                  <span> Product Accessories </span>
                  <div className="input__accessories">
                    {data?.accessories?.map((item) => (
                      <div key={item?.id} className="popup__input">
                        <div className="checkbox">
                          {/* Existing code for accessory checkbox */}
                        </div>
                        <div className="add__remove">
                          {/* New code for accessory quantity decrease */}
                          {/* Existing code for accessory quantity display */}
                          {/* New code for accessory quantity increase */}
                        </div>
                        {/* Existing code for input price */}
                      </div>
                    ))}
                  </div>
                </div>
              ) : null}
            </div>
          </div>
          <div className="review__section">
            {/* Existing code for review section */}
          </div>
        </div>
      </div>
    </>
  );
}

export default Popup;
