        try {
            const token = '241|99nrXNA44oarRLr4QFJueCwNxSabtqMuMlBAeDlV';
            const response = await fetch(`${Config.serverUrl}/api/user-favourites`, {
                method: 'POST',
                headers: {
                    Authorization: `Bearer ${token}`,
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ productid: productId }),
            });
            const jsonData = await response.json();
            console.log(jsonData);
            // Handle any further actions after adding the favorite product
        } catch (error) {
            console.error("Error adding favorite product:", error);
        }
    };

        const removeFavoriteProduct = async (productId) => {
            try {
                const token = '241|99nrXNA44oarRLr4QFJueCwNxSabtqMuMlBAeDlV';
                const response = await fetch(`${Config.serverUrl}/api/user-favourites/${productId}`, {
                    method: 'DELETE',
                    headers: {
                        Authorization: `Bearer ${token}`,
                        'Content-Type': 'application/json',
                    }
                });
                const jsonData = await response.json();
                console.log(jsonData);
                // Handle any further actions after adding the favorite product
            } catch (error) {
                console.error("Error adding favorite product:", error);
            }
        };
